import './App.css';
import { useState, useEffect } from 'react';
import { search } from './BooksAPI';
import { update } from './BooksAPI';
import { getAll } from './BooksAPI';

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [books, setBooks] = useState([]);
  const [allbooks, setAllBooks] = useState([]);
  const [input, setinput] = useState('');
  const [read, setRead] = useState([]);

  const searchhandler = (e) => {
    setinput(e.target.value);
    console.log(e.target.value);
  };

  const Books = async () => {
    const books = await search(input);
    setBooks(books);
    console.log(books);
  };

  const getAllBooks = async () => {
    const books = await getAll();
    setAllBooks(books);
    console.log(books);
  };

  const updateBook = async (book, ch) => {
    const data = await update(book, ch);
    console.log(data);
  };

  useEffect(() => {
    Books();
  }, [input]);

  useEffect(() => {
    getAllBooks();
    updateBook();
  }, [allbooks]);

  const readShelf = allbooks.filter((books) => books.shelf === 'read');
  const currentReadShelf = allbooks.filter(
    (books) => books.shelf === 'currentlyReading',
  );
  const wanttReadShelf = allbooks.filter(
    (books) => books.shelf === 'wantToRead',
  );
  // console.log(read);

  console.log(currentReadShelf);
  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                onChange={searchhandler}
                value={input}
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid">
              {Array.isArray(books)
                ? books?.map((book, idx) => {
                    return (
                      <li key={idx}>
                        <div className="book">
                          <div className="book-top">
                            <div
                              className="book-cover"
                              style={{
                                width: 128,
                                height: 193,
                                backgroundImage: `url(${book.imageLinks.thumbnail})`,
                              }}
                            ></div>
                            <div className="book-shelf-changer">
                              <select
                                onChange={(e) => {
                                  console.log(e.target.value);
